import React, { useState } from "react";
import { useSwipeable } from "react-swipeable";
import SequentialLottie from "./Lottie";
import AnimatedComponent from "./Test";

function App() {
    // üìå Ïó¨Îü¨ ÌôîÎ©¥ÏùÑ Î∞∞Ïó¥Î°ú Í¥ÄÎ¶¨
    const screens = [
        { title: "üéâ ÏÉùÏùºÏ∂ïÌïòÌï©ÎãàÎã§ Í∞ÄÎãàÏî®!", content: <SequentialLottie /> },
        { title: "üéÇ ÏÑúÌîÑÎùºÏù¥Ï¶à!", content: <AnimatedComponent /> },
        { title: "üéä ÎßàÏßÄÎßâ ÌôîÎ©¥!", content: <p>Ïó¨Í∏∞ÍπåÏßÄ ÏôîÏñ¥Ïöî! üéà</p> }, // Ï∂îÍ∞Ä Í∞ÄÎä•
    ];

    const [currentScreen, setCurrentScreen] = useState(0);

    // Ïä§ÏôÄÏù¥ÌîÑ Í∞êÏßÄ Ìï∏Îì§Îü¨
    const handlers = useSwipeable({
        onSwipedUp: () =>
            setCurrentScreen((prev) => Math.min(prev + 1, screens.length - 1)), // ÏïÑÎûòÎ°ú Ïä§ÏôÄÏù¥ÌîÑ ‚Üí Îã§Ïùå ÌôîÎ©¥ (ÏµúÎåÄ screens.length - 1)
        onSwipedDown: () => setCurrentScreen((prev) => Math.max(prev - 1, 0)), // ÏúÑÎ°ú Ïä§ÏôÄÏù¥ÌîÑ ‚Üí Ïù¥Ï†Ñ ÌôîÎ©¥ (ÏµúÏÜå 0)
        preventScrollOnSwipe: true,
    });

    return (
        <div
            {...handlers}
            className="w-screen h-screen flex flex-col items-center justify-center bg-gray-100 p-4"
        >
            <div className="bg-white rounded-2xl shadow-md w-full max-w-sm p-6">
                <h1>{screens[currentScreen].title}</h1>
                {screens[currentScreen].content}

                {/* Ïä§ÏôÄÏù¥ÌîÑ ÏïàÎÇ¥ ÌÖçÏä§Ìä∏ */}
                <p className="text-gray-500 text-sm mt-4">
                    {currentScreen < screens.length - 1
                        ? "ÏïÑÎûòÎ°ú Ïä§ÏôÄÏù¥ÌîÑÌïòÏÑ∏Ïöî üëá"
                        : "ÏúÑÎ°ú Ïä§ÏôÄÏù¥ÌîÑÌïòÎ©¥ ÎèåÏïÑÍ∞ëÎãàÎã§ ‚òùÔ∏è"}
                </p>
            </div>
        </div>
    );
}

export default App;
