                {isMuted && (
                    <button onClick={unmuteAudio} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
                        üîä ÏÜåÎ¶¨ ÏºúÍ∏∞
                    </button>
                )}
            </div>
        </div>
    );
}

export default App;
