import React, { useRef, useEffect } from "react";
import SequentialLottie from "./Lottie";
import AnimatedComponent from "./Test";
import { motion } from "framer-motion";

function App() {
    const audioRef = useRef(null);

    useEffect(() => {
        const playAudio = () => {
            if (audioRef.current) {
                audioRef.current.volume = 0.5; // 볼륨 조절 (0.0 ~ 1.0)
                audioRef.current.play().catch((error) => {
                    console.log("자동 재생이 차단됨:", error);
                });
            }
        };

        playAudio();
    }, []);

    return (
        <div className="w-full h-screen flex items-center justify-center bg-gray-100 p-4 overflow-hidden">
            {/* 배경을 위한 div */}
            <div className="fixed-bg"></div>

            {/* 배경 음악 (숨김) */}
            <audio ref={audioRef} src="/music.mp3" loop />

            {/* 스크롤 가능한 컨텐츠 박스 */}
            <div className="rounded-2xl shadow-md w-full max-w-sm p-6 flex flex-col items-center relative h-96 overflow-y-auto">
                <h1 style={{width: "50%", margin: "10px auto"}} className="w-1/2 h-auto my-4 mx-auto whitespace-nowrap">❤️생일축하해 가니❤️</h1>
                <SequentialLottie />
            </div>
        </div>
    );
}

export default App;
