import React, { useState } from "react";
import SequentialLottie from "./Lottie";
import AnimatedComponent from "./Test";
import { motion } from 'framer-motion';

function App() {
    const [isLottieLoaded, setIsLottieLoaded] = useState(false);

    return (
        <div className="w-full h-screen flex items-center justify-center bg-gray-100 p-4 overflow-hidden">
            {/* 배경을 위한 div */}
            <div className="fixed-bg"></div>

            {/* 스크롤 가능한 컨텐츠 박스 */}
            <div
                className="rounded-2xl shadow-md w-full max-w-sm p-6 flex flex-col items-center relative h-96 overflow-y-auto">
                <h1 className="block text-center">❤️생일축하해 가니❤️</h1>
                {/* Lottie 애니메이션 완료되면 상태 변경 */}
                <SequentialLottie onComplete={() => setIsLottieLoaded(true)} />

                {/* Lottie 완료 후 motion.div 나타남 */}
                {isLottieLoaded && (
                    <motion.div
                        initial={{ opacity: 0, x: 50 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{
                            ease: "easeInOut",
                            duration: 2,
                            x: { duration: 1 },
                        }}
                    >
                        <p>ㅗㅑㅗㅑㅑ</p>
                    </motion.div>
                )}
            </div>
        </div>
    );
}


export default App;
