import { useState, useRef } from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import t1 from "./t1.json";
import t2 from "./t2.json";
import i1 from "./img1.jpg";

const SequentialLottie = ({ onComplete }) => {
    const [showSecond, setShowSecond] = useState(false);
    const [isSecondStarted, setIsSecondStarted] = useState(false); // 두 번째 애니메이션이 한 번이라도 실행됐는지 여부
    const firstLottieRef = useRef(null);

    return (
        <div className="flex flex-col items-center">
            {/* 첫 번째 애니메이션 */}
            <Lottie
                lottieRef={firstLottieRef}
                animationData={t1}
                loop={false}
                onComplete={() => setShowSecond(true)} // 첫 번째 애니메이션이 끝나면 두 번째 애니메이션 실행
            />

            {/* 두 번째 애니메이션: 첫 번째가 끝난 후 실행됨 (무한 루프) */}
            {showSecond && (
                <Lottie
                    animationData={t2}
                    loop={true} // ❗ 무한 반복
                    onLoopComplete={() => setIsSecondStarted(true)} // 첫 루프가 끝나면 motion.div 표시
                />
            )}

            {/* 두 번째 애니메이션이 한 번이라도 실행되면 motion.div 표시 */}
            {isSecondStarted && (
                <motion.div
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{
                        ease: "easeInOut",
                        duration: 2,
                        x: { duration: 1 },
                    }}
                >
                    <p>자기야 하위~~!!</p>
                    <p>벌써 내가 자기 생일을 3번이나 챙겨줬네~! 시간이 너무 빨라?!</p>
                    <p>특히 이번에는 선물 고르는게 너무 어려웠어,,,, 그래서 고민하고 고민하다가 자기가 팔찌가 없는거 같아서 한번 골라봤습니당..!</p>
                    <p>오늘은 자기를 행복하게 해주고 싶어서 나도 어떻게 해줄까 고민을 많이 해보고 준비해본건데 (선물은 조금 마음에 안들지만...) 그래도
                        자기랑 앞으로도 계속 지금처럼만 행복하게 지내면 너무 좋을것같아! 우리가 만난지 벌써 834일이나 됐다?!
                        자기 처음 만났을때는 머리가 엄청 짧았는데 지금은 머리도 많이 길렀고!
                    </p>
                    <div>
                        <img src={i1} alt="1"/>

                    </div>

                </motion.div>
            )}
        </div>
    );
};

export default SequentialLottie;
