import { useState, useRef } from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import t1 from "./t1.json";
import t2 from "./t2.json";
import i1 from "./img1.jpg";
import i2 from "./img2.jpg";
import i3 from "./img3.jpg";
import i4 from "./img4.jpg";
import i5 from "./img4.jpg";

const SequentialLottie = ({ onComplete }) => {
    const [showSecond, setShowSecond] = useState(false);
    const [isSecondStarted, setIsSecondStarted] = useState(false); // 두 번째 애니메이션이 한 번이라도 실행됐는지 여부
    const firstLottieRef = useRef(null);

    return (
        <div className="flex flex-col items-center">
            {/* 첫 번째 애니메이션 */}
            <Lottie
                lottieRef={firstLottieRef}
                animationData={t1}
                loop={false}
                onComplete={() => setShowSecond(true)} // 첫 번째 애니메이션이 끝나면 두 번째 애니메이션 실행
            />

            {/* 두 번째 애니메이션: 첫 번째가 끝난 후 실행됨 (무한 루프) */}
            {showSecond && (
                <Lottie
                    animationData={t2}
                    loop={true} // ❗ 무한 반복
                    onLoopComplete={() => setIsSecondStarted(true)} // 첫 루프가 끝나면 motion.div 표시
                />
            )}

            {/* 두 번째 애니메이션이 한 번이라도 실행되면 motion.div 표시 */}
            {isSecondStarted && (
                <motion.div
                    initial={{opacity: 0, x: 50}}
                    animate={{opacity: 1, x: 0}}
                    transition={{
                        ease: "easeInOut",
                        duration: 2,
                        x: {duration: 1},
                    }}
                    style={{paddingLeft: "20px", paddingRight: "20px", textAlign: "center"}} // ✅ 좌우 여백 추가
                >
                    <h3 style={{marginBottom: "10px", fontSize: "18px", fontWeight: "600"}}>
                        To.세상에서 가장 소중한 가은이에게
                    </h3>

                    <p style={{marginBottom: "10px"}}>자기야! 생일 정말 정말 축하해! 🥳🎂🎉</p>

                    <p style={{marginBottom: "10px"}}>자기 처음 만났을때는 머리가 엄청 짧고 노랬는데</p>
                    <img src={i1} alt="1"
                         style={{width: "50%", height: "auto", display: "block", margin: "10px auto"}}/>

                    <p style={{marginBottom: "10px"}}>지금은 이렇게 길렀네??</p>
                    <img src={i4} alt="1"
                         style={{width: "50%", height: "auto", display: "block", margin: "10px auto"}}/>

                    <p style={{marginBottom: "10px"}}>속초도 놀러가구</p>
                    <img src={i2} alt="1"
                         style={{width: "50%", height: "auto", display: "block", margin: "10px auto"}}/>

                    <p style={{marginBottom: "10px"}}>코타키나발루도 놀러가구</p>
                    <img src={i3} alt="1"
                         style={{width: "50%", height: "auto", display: "block", margin: "10px auto"}}/>

                    <p style={{marginBottom: "10px", fontSize: "16px", fontWeight: "600"}}>
                        내가 너무 너무 사랑하고 오늘 생일 너무 축하해💕💕
                    </p>

                    <h3 style={{marginTop: "20px", fontSize: "18px", fontWeight: "600"}}>
                        From. 가은이를 세상에서 제일 제일 사랑하는 구승이..
                    </h3>
                </motion.div>

            )}
        </div>
    );
};

export default SequentialLottie;
