import React, { useState } from "react";
import { useSwipeable } from "react-swipeable";
import SequentialLottie from "./Lottie";
import AnimatedComponent from "./Test";

function App() {
    // 📌 여러 화면을 배열로 관리
    const screens = [
        { title: "🎉 생일축하합니다 가니씨!", content: <SequentialLottie /> },
        { title: "🎂 서프라이즈!", content: <AnimatedComponent /> },
        { title: "🎊 마지막 화면!", content: <p>여기까지 왔어요! 🎈</p> }, // 추가 가능
    ];

    const [currentScreen, setCurrentScreen] = useState(0);

    // 스와이프 감지 핸들러
    const handlers = useSwipeable({
        onSwipedUp: () => {
            setCurrentScreen((prev) => Math.min(prev + 1, screens.length - 1)); // 아래로 스와이프 → 다음 화면
        },
        onSwipedDown: () => {
            setCurrentScreen((prev) => Math.max(prev - 1, 0)); // 위로 스와이프 → 이전 화면
        },
        preventScrollOnSwipe: true, // ✅ 스크롤 방지
        trackTouch: true, // ✅ 터치 이벤트 추적 (스와이프 인식 강화)
        delta: 50, // ✅ 스와이프 감지 최소 거리 (값 조정 가능)
    });

    return (
        <div
            {...handlers}
            className="w-screen h-screen flex flex-col items-center justify-center bg-gray-100 p-4"
            style={{ touchAction: "none" }} // ✅ 스와이프 중 화면 흔들림 방지
        >
            <div className="bg-white rounded-2xl shadow-md w-full max-w-sm p-6">
                <h1>{screens[currentScreen].title}</h1>
                {screens[currentScreen].content}

                {/* 스와이프 안내 텍스트 */}
                <p className="text-gray-500 text-sm mt-4">
                    {currentScreen < screens.length - 1
                        ? "아래로 스와이프하세요 👇"
                        : "위로 스와이프하면 돌아갑니다 ☝️"}
                </p>
            </div>
        </div>
    );
}

export default App;
