import React, { useState, useRef } from "react";
import Lottie from "lottie-react";
import t1 from "./t1.json";
import t2 from "./t2.json";

const SequentialLottie = () => {
    const [showSecond, setShowSecond] = useState(false);
    const firstLottieRef = useRef(null);

    return (
        <div>
            {!showSecond ? (
                <Lottie
                    lottieRef={firstLottieRef}
                    animationData={t1}
                    loop={false}
                    onComplete={() => setShowSecond(true)} // 첫 번째 애니메이션이 끝나면 showSecond를 true로 변경
                />
            ) : (
                <Lottie animationData={t2} loop={true} />
            )}
        </div>
    );
};

export default SequentialLottie;
