import React, { useState } from "react";
import { useSwipeable } from "react-swipeable"; // 스와이프 감지 라이브러리
import SequentialLottie from "./Lottie";
import AnimatedComponent from "./AnimatedComponent";

function App() {
    const [showSecondScreen, setShowSecondScreen] = useState(false);

    // 스와이프 감지 핸들러
    const handlers = useSwipeable({
        onSwipedLeft: () => setShowSecondScreen(true), // 왼쪽으로 스와이프 시 두 번째 화면으로 이동
        onSwipedRight: () => setShowSecondScreen(false), // 오른쪽으로 스와이프 시 첫 번째 화면으로 이동
        preventScrollOnSwipe: true, // 스와이프 시 스크롤 방지
    });

    return (
        <div
            {...handlers}
            className="w-screen h-screen flex flex-col items-center justify-center bg-gray-100 p-4"
        >
            <div className="bg-white rounded-2xl shadow-md w-full max-w-sm p-6">
                {/* 첫 번째 화면 */}
                {!showSecondScreen ? (
                    <>
                        <h1>생일축하합니다 가니씨!</h1>
                        <SequentialLottie />
                        <p className="text-gray-500 text-sm mt-4">(왼쪽으로 스와이프하세요 👉)</p>
                    </>
                ) : (
                    /* 두 번째 화면 */
                    <>
                        <h1>서프라이즈! 🎉</h1>
                        <AnimatedComponent />
                        <p className="text-gray-500 text-sm mt-4">(오른쪽으로 스와이프하세요 👈)</p>
                    </>
                )}
            </div>
        </div>
    );
}

export default App;
